import { useParams } from "react-router-dom";
import { Row } from "react-bootstrap";
import ProductCard from "../ProductCard";
import PaginationControls from "../Pagination";

const SearchResultPage = ({ products }) => {
  const [currentPage, setCurrentPage] = useState(1);
  const { searchQuery } = useParams();
  const searchResults = products.filter(
    (product) =>
      product.en_name.toLowerCase().includes(searchQuery.toLowerCase()) ||
      product.ko_name.toLowerCase().includes(searchQuery.toLowerCase()) ||
      product.category.toLowerCase().includes(searchQuery.toLowerCase()) ||
      product.brand.toLowerCase().includes(searchQuery.toLowerCase())
  );

  const handlePageChange = (pageNumber) => {
    setCurrentPage(pageNumber);
  };

  const productsPerPage = 10;
  const totalPages = Math.ceil(searchResults.length / productsPerPage);

  const indexOfLastProduct = currentPage * productsPerPage;
  const indexOfFirstProduct = indexOfLastProduct - productsPerPage;
  const currentProducts = searchResults.slice(
    indexOfFirstProduct,
    indexOfLastProduct
  );

  return (
    <div>
      <h1 className="page-title">Search Results for "{searchQuery}"</h1>
      {searchResults.length > 0 && (
        <>
          <Row xs={1} md={2} lg={4} className="g-4 mx-1 my-2 m-xl-4">
            {currentProducts.map((result) => (
              <ProductCard key={result.id} product={result} />
            ))}
          </Row>
          {totalPages > 1 && (
            <PaginationControls
              currentPage={currentPage}
              totalPages={totalPages}
              onPageChange={handlePageChange}
            />
          )}
        </>
      )}
      {searchResults.length === 0 && <p>No results found</p>}
    </div>
  );
};

export default SearchResultPage;
