import { useParams } from "react-router-dom";
import { Row } from "react-bootstrap";
import ProductCard from "../ProductCard";

const SearchResultPage = ({ products }) => {
  const { searchQuery } = useParams();
  const searchResults = products.filter(
    (product) =>
      product.en_name.toLowerCase().includes(searchQuery.toLowerCase()) ||
      product.ko_name.toLowerCase().includes(searchQuery.toLowerCase()) ||
      product.category.toLowerCase().includes(searchQuery.toLowerCase()) ||
      product.brand.toLowerCase().includes(searchQuery.toLowerCase())
  );

  return (
    <div>
      <h1 className="page-title">Search Results for "{searchQuery}"</h1>
      {searchResults.length > 0 ? (
        <Row xs={1} md={2} lg={4} className="g-4 mx-1 my-2 m-xl-4">
          {searchResults.map((result) => (
            <ProductCard key={result.id} product={result} />
          ))}
        </Row>
      ) : (
        <p>No results found</p>
      )}
    </div>
  );
};

export default SearchResultPage;
