import { Container, Dropdown } from "react-bootstrap";

function Filter({ categories, brands, onCategoryChange, onBrandChange }) {
  return (
    <Container>
      <Dropdown>
        <Dropdown.Toggle variant="success" id="category-dropdown">
          Category
        </Dropdown.Toggle>
        <Dropdown.Menu>
          <Dropdown.Item onClick={() => onCategoryChange("all")}>
            All Categories
          </Dropdown.Item>
          {categories.map((category) => (
            <Dropdown.Item
              key={category}
              onClick={() => onCategoryChange(category)}
            >
              {category}
            </Dropdown.Item>
          ))}
        </Dropdown.Menu>
      </Dropdown>
      <Dropdown>
        <Dropdown.Toggle variant="success" id="brand-dropdown">
          Brand
        </Dropdown.Toggle>
        <Dropdown.Menu>
          <Dropdown.Item onClick={() => onBrandChange("all")}>
            All Brands
          </Dropdown.Item>
          {brands.map((brand) => (
            <Dropdown.Item key={brand} onClick={() => onBrandChange(brand)}>
              {brand}
            </Dropdown.Item>
          ))}
        </Dropdown.Menu>
      </Dropdown>
    </Container>
  );
}

export default Filter;
