import { configureStore, createSlice } from "@reduxjs/toolkit";

const cartItems = createSlice({
  name: "cartItems",
  initialState: [],
  reducers: {
    incrementCounter(state, action) {
      const productIndex = state.findIndex(
        (product) => product.id === action.payload
      );
      if (productIndex !== -1) {
        return state.map((product, index) =>
          index === productIndex
            ? { ...product, count: product.count + 1 }
            : product
        );
      }
      return state;
    },

    decrementCounter(state, action) {
      const productIndex = state.findIndex(
        (product) => product.id === action.payload
      );
      if (productIndex !== -1) {
        const updatedCount = state[productIndex].count - 1;

        if (updatedCount === 0) {
          return state.filter((product, index) => index !== productIndex);
        } else {
          return state.map((product, index) =>
            index === productIndex
              ? { ...product, count: updatedCount }
              : product
          );
        }
      }
      return state;
    },

    addToCart(state, action) {
      const existingProduct = state.find(
        (product) => product.id === action.payload
      );

      if (existingProduct) {
        return (existingProduct.count += 1);
      } else {
        state.push({ ...action.payload, count: 1 });
      }
    },

    updateCartItems(state, action) {
      console.log("Current state:", state);
      console.log("Updating cart items:", action.payload);
      const newState = action.payload;
      localStorage.setItem("addedToCart", JSON.stringify(newState));
      console.log("New state:", newState);
      return newState;
    },

    removeFromCart(state, action) {
      const productId = action.payload;
      const updatedCart = state.filter((product) => product.id !== productId);
      localStorage.setItem("addedToCart", JSON.stringify(updatedCart));
      return updatedCart;
    },
  },
});

export const {
  incrementCounter,
  decrementCounter,
  addToCart,
  updateCartItems,
  removeFromCart,
} = cartItems.actions;

export default configureStore({
  reducer: {
    cartItems: cartItems.reducer,
  },
});
