import { useEffect } from "react";
import { Table, Button, Container, Image } from "react-bootstrap";
import { useSelector, useDispatch } from "react-redux";
import {
  incrementCounter,
  decrementCounter,
  updateCartItems,
  removeFromCart,
} from "../../store";

function CartPage() {
  const dispatch = useDispatch();

  useEffect(() => {
    const storedCartItems = JSON.parse(localStorage.getItem("addedToCart"));
    console.log("storedCartItems:", storedCartItems);
    if (storedCartItems) {
      dispatch(updateCartItems(storedCartItems));
    }
  }, [dispatch]);

  const cartItems = useSelector((state) => state.cartItems);

  const totalPrice = cartItems.reduce((sum, product) => {
    return sum + product.count * Number(product.price);
  }, 0);

  return (
    <Container fluid>
      <h1 className="page-title"> Cart</h1>
      <Table className="mt-4 table">
        <thead>
          <tr>
            <th></th>
            <th>Product</th>
            <th>Quantity</th>
            <th>Price </th>
            <th></th>
          </tr>
        </thead>
        <tbody>
          {cartItems.map((product) => {
            return (
              <tr key={product.id}>
                <td className="">
                  <Image src={product.image} alt={product.en_name} fluid />
                </td>
                <td className="p-0">
                  <div className="fw-bold"> {product.en_name}</div>
                  <div className="fw-bold">{product.ko_name}</div>
                </td>
                <td>
                  <Button
                    onClick={() => {
                      dispatch(decrementCounter(product.id));
                    }}
                    className="cart-button me-1"
                  >
                    -
                  </Button>
                  {product.count}
                  <Button
                    onClick={() => {
                      dispatch(incrementCounter(product.id));
                    }}
                    className="cart-button ms-1"
                  >
                    +
                  </Button>{" "}
                </td>
                <td className="p-0 text-center">
                  ${product.count * product.price}
                </td>
                <td>
                  <Button
                    onClick={() => {
                      dispatch(removeFromCart(product.id));
                    }}
                  >
                    Remove
                  </Button>
                </td>
              </tr>
            );
          })}
        </tbody>
      </Table>
      <div className="total-price">
        <p>Total price: ${totalPrice}</p>
        <p>(Incl. GST)</p>
      </div>

      <Button>Continue Shopping</Button>
      <Button>Checkout</Button>
    </Container>
  );
}

export default CartPage;
