import { useEffect } from "react";
import { Table, Button, Container, Image } from "react-bootstrap";
import { useSelector, useDispatch } from "react-redux";
import {
  incrementCounter,
  decrementCounter,
  removeFromCart,
  addToCart,
} from "../../store";
import { Link } from "react-router-dom";

function CartPage() {
  const dispatch = useDispatch();
  const cartItems = useSelector((state) => state.cartItems);

  useEffect(() => {
    const storedCartItems = JSON.parse(localStorage.getItem("addedToCart"));
    if (storedCartItems) {
      storedCartItems.forEach((item) => {
        if (!cartItems.some((cartItem) => cartItem.id === item.id)) {
          dispatch(addToCart(item));
        }
      });
    }
    // localStorage.setItem("addedToCart", JSON.stringify(cartItems));
  }, [dispatch, cartItems]);
  // useEffect(() => {
  //   const storedCartItems =
  //     JSON.parse(localStorage.getItem("addedToCart")) || [];
  //   storedCartItems.forEach((item) => {
  //     dispatch(addToCart(item));
  //   });
  // }, [dispatch]);

  // // Update local storage whenever cartItems change
  // useEffect(() => {
  //   localStorage.setItem("addedToCart", JSON.stringify(cartItems));
  // }, [cartItems]);

  const totalPrice = cartItems.reduce((sum, product) => {
    return sum + product.count * Number(product.price);
  }, 0);

  return (
    <Container fluid>
      <h1 className="page-title"> Cart</h1>
      <Table className="mt-4 table">
        <thead>
          <tr>
            <th></th>
            <th>Product</th>
            <th>Quantity</th>
            <th>Price </th>
            <th></th>
          </tr>
        </thead>
        <tbody>
          {cartItems.map((product) => {
            return (
              <tr key={product.id}>
                <td>
                  <Image
                    className="fixed-height-image"
                    src={product.image}
                    alt={product.en_name}
                    fluid
                  />
                </td>
                <td>
                  <div className="fw-bold"> {product.en_name}</div>
                  <div className="fw-bold">{product.ko_name}</div>
                </td>
                <td>
                  <Button
                    onClick={() => {
                      dispatch(decrementCounter(product.id));
                    }}
                    className="cart-button me-1"
                  >
                    -
                  </Button>
                  {product.count}
                  <Button
                    onClick={() => {
                      dispatch(incrementCounter(product.id));
                    }}
                    className="cart-button ms-1"
                  >
                    +
                  </Button>{" "}
                </td>
                <td className="text-center fw-bold">
                  ${product.count * product.price}
                </td>
                <td>
                  <Button
                    onClick={() => {
                      dispatch(removeFromCart(product.id));
                    }}
                    className="remove-btn p-1"
                  >
                    Remove
                  </Button>
                </td>
              </tr>
            );
          })}
        </tbody>
      </Table>
      <Container className="text-end fw-bold mb-4" style={{ fontSize: "14px" }}>
        <p className="m-0">Total price: ${totalPrice}</p>
        <p>(Incl. GST)</p>
      </Container>
      <Container className="d-flex justify-content-between align-items-center">
        <Button className="continue-shopping-btn">
          <Link to="/" className="text-white text-decoration-none">
            Continue Shopping
          </Link>
        </Button>
        <Button className="checkout-btn">
          <Link to="/" className="text-white text-decoration-none">
            Checkout
          </Link>
        </Button>
      </Container>
    </Container>
  );
}

export default CartPage;
