import "./App.css";
import { useState } from "react";
function App() {
  let [post, setPost] = useState([
    "ÎÇ®ÏûêÏΩîÌä∏ Ï∂îÏ≤ú",
    "Í∞ïÎÇ® Ïö∞Îèô ÎßõÏßë",
    "ÌååÏù¥Ïç¨ÎèÖÌïô",
  ]);
  let [like, setLike] = useState(0);
  let [modal, setModal] = useState(false);
  let [changeTitle, setChangeTitle] = useState(0);
  let [newPost, setNewPost] = useState("");

  return (
    <div className="App">
      <div className="black-nav">
        <div>ReactBlog</div>
      </div>
      <button
        onClick={() => {
          let sortPost = [...post];
          setPost(sortPost.sort());
        }}
      >
        sort
      </button>
      <button
        onClick={() => {
          let titles = [...post];
          titles[0] = "Recommend female coat";
          setPost(titles);
        }}
      >
        button
      </button>
      <button onClick={() => setModal(true)}>modal</button>
      {post.map((posts, i) => {
        return (
          <div className="list" key={i}>
            <h4
              onClick={() => {
                setModal(true);
                setChangeTitle(i);
              }}
            >
              {post[i]}
              <span
                onClick={() => {
                  setLike(like + 1);
                }}
              >
                üëç
              </span>
              {like}
            </h4>
            <p>2Ïõî 17Ïùº Î∞úÌñâ</p>
            <button
              onClick={() => {
                newPost(post(i).pop());
              }}
            >
              Delete
            </button>
          </div>
        );
      })}
      <input
        onChange={(e) => {
          setNewPost(e.target.value);
          console.log(newPost);
        }}
      />

      <button
        onClick={() => {
          post.push(newPost);
        }}
      >
        Add
      </button>
      {modal === true ? (
        <Modal changeTitle={changeTitle} setPost={setPost} post={post} />
      ) : null}
    </div>
  );
}

function Modal(props) {
  return (
    <div className="modal">
      <h4>{props.post[props.changeTitle]}</h4>
      <p>ÎÇ†Ïßú</p>
      <p>ÏÉÅÏÑ∏ÎÇ¥Ïö©</p>
      <button
        onClick={() => {
          let post = [...props.post];
          post[0] = "Recommend female coat";
          props.setPost(post);
        }}
      >
        Edit
      </button>
    </div>
  );
}
export default App;
