import { Card, Button, Stack } from "react-bootstrap";

import { useParams } from "react-router-dom";

function ProductDetailPage({ products }) {
  let { id } = useParams();
  const product = products.find((product) => product.id === id);

  return (
    <>
      <Stack direction="vertical" className="m-2">
        <Card.Body className="d-flex flex-column">
          <Card.Img
            src={product.image}
            alt={product.name}
            className="img-fluid"
          />

          <Card.Title className="flex-shrink-1 fw-bold product-title">
            {product.en_name}
          </Card.Title>
          <Card.Title className="flex-shrink-1 fw-bold product-title">
            {product.ko_name}
          </Card.Title>
          <Card.Subtitle className="mb-2 text-muted flex-shrink-1">
            {product.description}
          </Card.Subtitle>
          <Card.Text className="flex-grow-1 overflow-auto fs-6 fw-bold">
            {product.price}
          </Card.Text>

          <Button className="mt-2 align-self-end w-100 fw-bold">
            Add to cart
          </Button>
        </Card.Body>
      </Stack>
    </>
  );
}

export default ProductDetailPage;
