import { useEffect } from "react";
import { Table, Button } from "react-bootstrap";
import { useSelector, useDispatch } from "react-redux";
import {
  incrementCounter,
  decrementCounter,
  updateCartItems,
} from "../../store";

function CartPage() {
  const dispatch = useDispatch();

  useEffect(() => {
    const storedCartItems = JSON.parse(localStorage.getItem("addedToCart"));
    console.log("storedCartItems:", storedCartItems);
    dispatch(updateCartItems(storedCartItems));
  }, [dispatch]);

  const cartItems = useSelector((state) => state.cartItems);

  return (
    <>
      <h1 className="page-title"> Cart</h1>
      <Table responsive className="mt-4">
        <thead>
          <tr>
            <th></th>
            <th>Product</th>
            <th>Quantity</th>
            <th>Count </th>
          </tr>
        </thead>
        <tbody>
          {cartItems.map((product) => {
            return (
              <tr key={product.id}>
                <td>
                  <img
                    src={product.image}
                    alt={product.en_name}
                    // className="fixed-height-image"
                  />
                </td>
                <td>
                  <tr className="fw-bold"> {product.en_name}</tr>
                  <tr className="fw-bold">{product.ko_name}</tr>
                </td>
                <td>{product.count}</td>
                <td>
                  <Button
                    onClick={() => {
                      dispatch(decrementCounter(product.id));
                    }}
                    className="mx-2"
                  >
                    -
                  </Button>
                  {product.count}
                  <Button
                    onClick={() => {
                      dispatch(incrementCounter(product.id));
                    }}
                    className="mx-2"
                  >
                    +
                  </Button>{" "}
                </td>
              </tr>
            );
          })}
        </tbody>
      </Table>
    </>
  );
}

export default CartPage;
