import { configureStore, createSlice } from "@reduxjs/toolkit";

const initialCartItems = JSON.parse(localStorage.getItem("addedToCart")) || [];

let products = createSlice({
  name: "products",
  initialState: initialCartItems,
  reducers: {
    incrementCounter(state, action) {
      let product = state.find((product) => {
        return product.id === action.payload;
      });
      product.count++;
    },
    decrementCounter(state, action) {
      let product = state.find((product) => {
        return product.id === action.payload;
      });
      if (product.count > 0) {
        product.count--;
      }
      return state;
    },

    addToCart(state, action) {
      let product = state.find((product) => {
        return product.id === action.payload;
      });
      if (product) {
        product.count++;
      } else state.push({ ...action.payload, count: 1 });

      localStorage.setItem("addedToCart", JSON.stringify(state));
    },
  },
});
export let { incrementCounter, decrementCounter, addToCart } = products.actions;
export default configureStore({
  reducer: {
    products: products.reducer,
  },
});
