import { Dropdown, DropdownButton } from "react-bootstrap";

function Sort({ sortBy, sortOrder, onSortChange }) {
  return (
    <DropdownButton id="sort-dropdown" title="Relevance" variant="light">
      <Dropdown.Item onClick={() => onSortChange("relevance")}>
        Relevance{" "}
        {sortBy === "relevance" && <SortOrderIndicator sortOrder={sortOrder} />}
      </Dropdown.Item>
      <Dropdown.Item onClick={() => onSortChange("price")}>
        Price Low to High{" "}
        {sortBy === "price" && sortOrder === "asc" && (
          <SortOrderIndicator sortOrder={sortOrder} />
        )}
      </Dropdown.Item>
      <Dropdown.Item onClick={() => onSortChange("priceDesc")}>
        Price High to Low{" "}
        {sortBy === "priceDesc" && <SortOrderIndicator sortOrder={sortOrder} />}
      </Dropdown.Item>
    </DropdownButton>
  );
}

function SortOrderIndicator({ sortOrder }) {
  return sortOrder === "asc" ? <span>&#9660;</span> : <span>&#9650;</span>;
}

export default Sort;
