import { Dropdown, DropdownButton } from "react-bootstrap";
import { FunnelFill } from "react-bootstrap-icons";
import { useDispatch, useSelector } from "react-redux";
import { setBrand, setCategory } from "../store";

function Filter({ categories, brands }) {
  const dispatch = useDispatch();
  const { category, brand } = useSelector((state) => state.filters);
  return (
    <DropdownButton
      id="dropdown-item-button"
      variant="light"
      title={
        <span>
          <FunnelFill className="me-2" />
          Filter
        </span>
      }
      className="ms-3"
    >
      <Dropdown.Header
        onClick={() => onCategoryChange("all")}
        className="fw-bold"
      >
        All Categories
      </Dropdown.Header>
      <Dropdown.Item onClick={() => onCategoryChange("newArrival")}>
        New Arrival
      </Dropdown.Item>
      <Dropdown.Item onClick={() => onCategoryChange("special")}>
        Special
      </Dropdown.Item>
      <Dropdown.Item onClick={() => onCategoryChange("noodle")}>
        Noodle
      </Dropdown.Item>
      <Dropdown.Item onClick={() => onCategoryChange("snack")}>
        Snack
      </Dropdown.Item>
      <Dropdown.Item onClick={() => onCategoryChange("sauce")}>
        Sauce
      </Dropdown.Item>

      <Dropdown.Header className="fw-bold mt-3">Brands</Dropdown.Header>
      {brands.map((brand) => (
        <Dropdown.Item key={brand} onClick={() => onBrandChange(brand)}>
          {brand}
        </Dropdown.Item>
      ))}
    </DropdownButton>
  );
}

export default Filter;
