import { Card, Button } from "react-bootstrap";
import { useParams } from "react-router-dom";

function ProductDetailPage({ product }) {
  let { id } = useParams();
  return (
    <>
      <Card className="h-100 d-flex flex-column product-card">
        <Card.Body className="d-flex flex-column">
          <Row>
            <Col className="p-1">
              <Card.Img
                src={product.image}
                alt={product.name}
                className="img-fluid"
              />
            </Col>
            <Col className="p-1">
              <Card.Title className="flex-shrink-1 fw-bold product-title">
                {product.en_name}
              </Card.Title>
              <Card.Title className="flex-shrink-1 fw-bold product-title">
                {product.ko_name}
              </Card.Title>
              <Card.Subtitle className="mb-2 text-muted flex-shrink-1">
                {product.description}
              </Card.Subtitle>
              <Card.Text className="flex-grow-1 overflow-auto fs-6 fw-bold">
                {product.price}
              </Card.Text>
            </Col>
          </Row>
          <Row className="mt-auto">
            <Col>
              <Button className="mt-2 align-self-end w-100 fw-bold">
                Add to cart
              </Button>
            </Col>
          </Row>
        </Card.Body>
      </Card>
    </>
  );
}

export default ProductDetailPage;
