import { configureStore, createSlice } from "@reduxjs/toolkit";

let cartItems = createSlice({
  name: "cartItems",
  initialState: [],
  reducers: {
    incrementCounter(state, action) {
      let product = state.find((product) => {
        return product.id === action.payload;
      });
      product.count++;
    },
    decrementCounter(state, action) {
      let product = state.find((product) => {
        return product.id === action.payload;
      });
      if (product && product.count > 1) {
        return state.map((item) =>
          item.id === action.payload ? { ...item, count: item.count - 1 } : item
        );
      }
      return state;
    },

    addToCart(state, action) {
      let product = state.find((product) => {
        return product.id === action.payload;
      });
      if (product) {
        return incrementCounter(state, action);
      } else localStorage.setItem("addedToCart", JSON.stringify(product));
    },
  },
});
export let { incrementCounter, decrementCounter, addToCart } =
  cartItems.actions;
export default configureStore({
  reducer: {
    products: cartItems.reducer,
  },
});
