import { Col, Row, Card, Badge } from "react-bootstrap";
import AddToCartButton from "../addToCartButton";

function Special({ products }) {
  const specialProducts = products.filter(
    (product) => product.special === true
  );
  return (
    <>
      <h1 className="page-title"> Special</h1>

      <Col key={specialProducts.id} xs={12} md={6} lg={3}>
        <Card className="d-flex flex-column p-1 product-card ">
          {specialProducts && (
            <Badge pill bg="info" className="special-badge">
              Special
            </Badge>
          )}
          <Card.Body className="d-flex flex-column flex-1 justify-content-between">
            <Row>
              <Col className="p-1">
                <Card.Img
                  src={specialProducts.image}
                  alt={specialProducts.en_name}
                  className="img-fluid fixed-height-image"
                />
              </Col>
              <Col className="p-1">
                <Card.Title className="flex-shrink-1 fw-bold product-title">
                  {specialProducts.en_name}
                </Card.Title>
                <Card.Title className="flex-shrink-1 fw-bold product-title">
                  {specialProducts.ko_name}
                </Card.Title>
                <Card.Subtitle className="mb-2 text-muted flex-shrink-1">
                  {specialProducts.description}
                </Card.Subtitle>
                <Card.Text className="fs-6 fw-bold">
                  ${specialProducts.price}
                </Card.Text>
              </Col>
            </Row>
            <Row>
              <Col>
                <AddToCartButton product={specialProducts} />
              </Col>
            </Row>
          </Card.Body>
        </Card>
      </Col>
    </>
  );
}

export default Special;
