import { Dropdown, DropdownButton } from "react-bootstrap";
import { FunnelFill } from "react-bootstrap-icons";
import { useDispatch, useSelector } from "react-redux";
import { setBrand, setCategory } from "../store";

function Filter({ categories, brands }) {
  const dispatch = useDispatch();
  const { category, brand } = useSelector((state) => state.filters);

  const handleCategoryChange = (category) => {
    dispatch(setCategory(category));
  };

  const handleBrandChange = (brand) => {
    dispatch(setBrand(brand));
  };

  return (
    <DropdownButton
      id="dropdown-item-button"
      variant="light"
      title={
        <span>
          <FunnelFill className="me-2" />
          Filter
        </span>
      }
      className="ms-3"
    >
      <Dropdown.Header
        onClick={() => handleCategoryChange("all")}
        className="fw-bold"
      >
        All Categories
      </Dropdown.Header>
      <Dropdown.Item onClick={() => handleCategoryChange("newArrival")}>
        New Arrival
      </Dropdown.Item>
      <Dropdown.Item onClick={() => handleCategoryChange("special")}>
        Special
      </Dropdown.Item>
      <Dropdown.Item onClick={() => handleCategoryChange("noodle")}>
        Noodle
      </Dropdown.Item>
      <Dropdown.Item onClick={() => handleCategoryChange("snack")}>
        Snack
      </Dropdown.Item>
      <Dropdown.Item onClick={() => handleCategoryChange("sauce")}>
        Sauce
      </Dropdown.Item>

      <Dropdown.Header className="fw-bold mt-3">Brands</Dropdown.Header>
      {brands.map((brand) => (
        <Dropdown.Item key={brand} onClick={() => handleBrandChange(brand)}>
          {brand}
        </Dropdown.Item>
      ))}
    </DropdownButton>
  );
}

export default Filter;
