import { Button } from "react-bootstrap";
import { useSelector, useDispatch } from "react-redux";
import { addToCart, decrementCounter, incrementCounter } from "../store";

function AddToCartButton({ product }) {
  let dispatch = useDispatch();
  let cartItems = useSelector((state) => {
    state.cartItems.find((cartItem) => cartItem.id === product.id);
  });

  const handleAddToCart = () => {
    dispatch(addToCart(product));
  };

  return (
    <>
      {cartItems ? (
        <>
          <Button
            onClick={() => {
              dispatch(decrementCounter(product.id));
            }}
          >
            -
          </Button>
          {cartItems.count}
          <Button
            onClick={() => {
              dispatch(incrementCounter(product.id));
            }}
          >
            +
          </Button>
        </>
      ) : (
        <Button
          onClick={() => {
            dispatch(addToCart(product));
          }}
          className="mt-2 align-self-end w-100 fw-bold"
        >
          Add to cart
        </Button>
      )}
    </>
  );
}

export default AddToCartButton;
