import { Dropdown, DropdownButton } from "react-bootstrap";

function Sort({ sortBy, sortOrder, onSortChange }) {
  const sortOptions = [
    { key: "relevance", label: "Relevance" },
    { key: "salePrice", label: "Price Low to High" },
    { key: "salePriceDesc", label: "Price High to Low" },
  ];
  return (
    <DropdownButton id="sort-dropdown" title="Relevance" variant="light">
      {sortOptions.map((option) => (
        <Dropdown.Item
          key={option.key}
          onClick={() => onSortChange(option.key)}
        >
          {option.label}{" "}
          {sortBy === option.key && sortOrder === "asc" && (
            <SortOrderIndicator sortOrder={sortOrder} />
          )}
        </Dropdown.Item>
      ))}
    </DropdownButton>
  );
}

function SortOrderIndicator({ sortOrder }) {
  return sortOrder === "asc" ? <span>&#9660;</span> : <span>&#9650;</span>;
}

