import { Card, Row, Col, Button } from "react-bootstrap";

function ProductCard({ products }) {
  return (
    <>
      <Row xs={1} md={2} xl={4} className="g-4 m-2">
        {products.map((product, i) => (
          <Col key={i}>
            <Card className="h-100 d-flex flex-column">
              <Card.Body className="d-flex flex-column">
                <Row>
                  <Col className="p-0">
                    <Card.Img
                      src={product.image}
                      alt={product.name}
                      className="img-fluid"
                    />
                  </Col>
                  <Col className="p-0">
                    <Card.Title className="flex-shrink-1 fs-6 fw-bold">
                      {product.name}
                    </Card.Title>
                    <Card.Subtitle className="mb-2 text-muted flex-shrink-1">
                      {product.description}
                    </Card.Subtitle>
                    <Card.Text className="flex-grow-1 overflow-auto fw-bold">
                      {product.price}
                    </Card.Text>
                  </Col>
                </Row>
                <Row className="mt-auto">
                  <Col>
                    <Button className="mt-2 align-self-end w-100">
                      Add to cart
                    </Button>
                  </Col>
                </Row>
              </Card.Body>
            </Card>
          </Col>
        ))}
      </Row>
    </>
  );
}

export default ProductCard;
