import { Card, Row, Col } from "react-bootstrap";
import AddToCartButton from "./addToCartButton";

function ProductCard({ product }) {
  return (
    <Col key={product.id}>
      <Card className="h-100 d-flex flex-column product-card">
        <Card.Body className="d-flex flex-column">
          <Row>
            <Col className="p-1">
              <Card.Img
                src={product.image}
                alt={product.en_name}
                className="img-fluid"
              />
            </Col>
            <Col className="p-1">
              <Card.Title className="flex-shrink-1 fw-bold product-title">
                {product.en_name}
              </Card.Title>
              <Card.Title className="flex-shrink-1 fw-bold product-title">
                {product.ko_name}
              </Card.Title>
              <Card.Subtitle className="mb-2 text-muted flex-shrink-1">
                {product.description}
              </Card.Subtitle>
              <Card.Text className="flex-grow-1 overflow-auto fs-6 fw-bold">
                {product.price}
              </Card.Text>
            </Col>
          </Row>
          <Row className="mt-auto">
            <Col>
              <AddToCartButton className="mt-2 align-self-end w-100 fw-bold">
                Add to cart
              </AddToCartButton>
            </Col>
          </Row>
        </Card.Body>
      </Card>
    </Col>
  );
}

export default ProductCard;
